mapper.select:
SELECT
FROM t_tutor t JOIN t_tutor_subject ts ON t.id = ts.tutor_id JOIN t_subject s ON ts.subject_id = s.id JOIN t_tutor_grade tg ON t.id = tg.tutor_id JOIN t_grade g ON tg.grade_id = g.id JOIN t_tutor_distr td ON t.id = td.tutor_id JOIN t_district d ON td.distr_id = d.id JOIN t_tutor_mode tm ON t.id = tm.tutor_id JOIN t_mode m ON tm.mode_id = m.id
t.id IN ( SELECT ids.id FROM ( SELECT tt.id FROM t_tutor tt
JOIN t_tutor_subject tts ON tt.id = tts.tutor_id AND tts.subject_id = #{subjectId, jdbcType=INTEGER}
JOIN t_tutor_grade ttg ON tt.id = ttg.tutor_id AND ttg.grade_id = #{gradeId, jdbcType=INTEGER}
JOIN t_tutor_distr ttd ON tt.id = ttd.tutor_id AND ttd.distr_id = #{distrId, jdbcType=INTEGER}
JOIN t_tutor_mode ttm ON tt.id = ttm.tutor_id AND ttm.mode_id = #{modeId, jdbcType=INTEGER}
tt.gender = #{gender, jdbcType=VARCHAR}
AND tt.tutor_type = #{tutorType, jdbcType=INTEGER}
AND tt.teach_city_id = #{teachCityId, jdbcType=INTEGER}
ORDER BY tt.last_login DESC
LIMIT #{start, jdbcType=INTEGER}, #{num, jdbcType=INTEGER}
) ids )
ORDER BY t.last_login DESC

